window.addEventListener("DOMContentLoaded",()=>{

    gsap.registerPlugin(ScrambleTextPlugin,ScrollTrigger);
  
    document.querySelectorAll("[data-scramble]").forEach(e=>{
  
      let t=e.dataset.text||e.textContent,
          a=e.dataset.chars||"upperAndLowerCase",
          r=parseFloat(e.dataset.delay)||0,
          l=parseFloat(e.dataset.duration)||1;
  
      gsap.to(e,{
        duration:l,
        delay:r,
        scrambleText:{
          text:t,
          chars:a
        },
        scrollTrigger:{
          trigger:e,
          start:e.dataset.start||"top 80%",
          toggleActions:e.dataset.toggle||"play none none none"
        }
      });
  
    });
  
  });
  