window.addEventListener("DOMContentLoaded",()=>{gsap.registerPlugin(ScrambleTextPlugin,ScrollTrigger);document.querySelectorAll("[data-scramble]").forEach(e=>{let t=e.dataset.text||"Default Text",a=e.dataset.chars||"upperAndLowerCase",r=parseFloat(e.dataset.delay)||0,l=parseFloat(e.dataset.duration)||1;gsap.to(e,{duration:l,delay:r,scrambleText:{text:t,chars:a},scrollTrigger:{trigger:e,start:"top 80%",toggleActions:"play none none none"}})})});
